    Author: Marcel Haag
    Issue date: Sat 21 May, 2022
*/

//Successful to create new user with status code is 201
insomnia.test("Successful to create new user with status code is 201", function () {
    insomnia.response.to.have.status(201);
});

var jsonData = JSON.parse(insomnia.response.text());
//Verify that the new user is create with the correct email
insomnia.test("TVerify that the new user is create with the correct email", function () {
    insomnia.expect(jsonData.data.email).is.equal(insomnia.environment.get("_email"));
});

